function getMemberTemplate(){$.ajax({url:"tmpl/member.tmpl",dataType:"html",success:function(a){$("head").append(a),updateMemberTable()}})}function buildMemberRows(a){return _.template($("#member-tmpl").html(),{members:a})}function updateMemberTable(){$.ajax({url:"http://poh5-aerogear.rhcloud.com/rest/members/json",cache:!1,success:function(a){$("#members").empty().append(buildMemberRows(a))},error:function(a){}})}function registerMember(a){$("span.invalid").remove(),$("span.success").remove(),$.post("http://poh5-aerogear.rhcloud.com/rest/members",a,function(a){$("#reg")[0].reset(),$("#formMsgs").append($('<span class="success">Member Registered</span>')),updateMemberTable()}).error(function(a){if(a.status==409||a.status==400){var b=$.parseJSON(a.responseText);$.each(b,function(a,b){$('<span class="invalid">'+b+"</span>").insertAfter($("#"+a))})}else $("#formMsgs").append($('<span class="invalid">Unknown server error</span>'))})}